package operators;

public class calc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=15,b=10;
		System.out.println("sum="+(a+b));
		System.out.println("sub="+(a-b));
		System.out.println("mult="+(a*b));
		System.out.println("div="+(a/b));
		System.out.println("mod="+(a%b));
	}

}
